mapper.select:
SELECT *
FROM (
         SELECT *
         FROM gen_and_age
         WHERE (gubun, create_dt) in (
             SELECT gubun, max(create_dt) as create_dt
             FROM gen_and_age
             WHERE date(create_dt) = date(#{createDt}) AND gubun = #{genOrAge}
             GROUP BY gubun
         )
     ) AS temp
GROUP BY gubun;

mapper.delete:
DELETE
FROM gen_and_age
WHERE date(create_dt) = date(#{createDt});
